// Treebeard Init - Patience & Rate Limit Handler
const input = $input.first().json;

const context = {
  agent: 'treebeard',
  tier: 'situational',
  hackathon_id: input.hackathon_id,
  triggered_at: input.triggered_at || new Date().toISOString(),
  trigger_reason: input.trigger_reason || 'rate_limited',

  // Rate limit context
  rate_limit_info: input.rate_limit_info || {},
  wait_duration_ms: input.wait_duration_ms || 60000,
  pending_tasks: input.pending_tasks || [],

  // Current state
  current_phase: input.current_phase || 'unknown',
  completed_files: input.completed_files || 0,
  total_files: input.total_files || 0,

  // What we can do while waiting
  available_actions: [
    'reorganize_queue_priorities',
    'precompute_dependencies',
    'review_completed_work',
    'optimize_remaining_plan'
  ]
};

return { json: context };

// Aragorn Init - Leadership Decision Handler
const input = $input.first().json;

const context = {
  agent: 'aragorn',
  tier: 'situational',
  hackathon_id: input.hackathon_id,
  triggered_at: input.triggered_at || new Date().toISOString(),
  trigger_reason: input.trigger_reason || 'decision_pending',

  // Decision context
  decision_type: input.decision_type || 'architecture_conflict',
  options: input.options || [],
  stakeholders: input.stakeholders || ['gimli', 'legolas'],

  // Conflict details
  conflict_description: input.conflict_description || '',
  affected_files: input.affected_files || [],
  blocking_tasks: input.blocking_tasks || [],

  // Project context
  project_goals: input.project_goals || [],
  deadline_pressure: input.deadline_pressure || 'normal',
  budget_remaining: input.budget_remaining || 0
};

return { json: context };

// Eowyn Init - Unconventional Solutions Handler
const input = $input.first().json;

const context = {
  agent: 'eowyn',
  tier: 'situational',
  hackathon_id: input.hackathon_id,
  triggered_at: input.triggered_at || new Date().toISOString(),
  trigger_reason: input.trigger_reason || 'impossible_block',

  // Block details
  block_description: input.block_description || '',
  conventional_attempts: input.conventional_attempts || [],
  why_impossible: input.why_impossible || '',

  // Constraints
  hard_constraints: input.hard_constraints || [],
  soft_constraints: input.soft_constraints || [],

  // Available resources
  available_tools: input.available_tools || [],
  time_remaining: input.time_remaining || 0,

  // Creative freedom
  allow_scope_reduction: input.allow_scope_reduction !== false,
  allow_alternative_tech: input.allow_alternative_tech !== false,
  allow_pivot: input.allow_pivot || false
};

return { json: context };

// Gollum Init - Edge Case Testing Handler
const input = $input.first().json;

const context = {
  agent: 'gollum',
  tier: 'support',
  hackathon_id: input.hackathon_id,
  triggered_at: input.triggered_at || new Date().toISOString(),

  // Code to analyze
  file_path: input.file_path || '',
  file_content: input.file_content || '',
  file_type: input.file_type || 'unknown',

  // Function/component context
  function_name: input.function_name || '',
  input_types: input.input_types || [],
  expected_outputs: input.expected_outputs || [],

  // Edge case categories to explore
  categories: [
    'null_undefined_inputs',
    'empty_collections',
    'boundary_values',
    'type_coercion',
    'concurrent_access',
    'unicode_special_chars',
    'large_inputs',
    'negative_numbers',
    'date_edge_cases',
    'network_failures'
  ],

  // Existing tests to avoid duplication
  existing_tests: input.existing_tests || []
};

return { json: context };

// Pippin Init - Serendipity/Random Exploration Handler
const input = $input.first().json;

const context = {
  agent: 'pippin',
  tier: 'support',
  hackathon_id: input.hackathon_id,
