};

return { json: context };

// Thorin Init - Test Writer
const input = $input.first().json;

const context = {
  agent: 'thorin',
  tier: 'support',
  hackathon_id: input.hackathon_id,
  triggered_at: input.triggered_at || new Date().toISOString(),

  // Test plan from Arwen
  test_plan: input.test_plan || {},

  // Current test to write
  test_file_path: input.test_file_path || '',
