  // Build info
  build_command: input.build_command || 'npm run build',
  output_directory: input.output_directory || 'dist',

  // Environment
  environment_variables: input.environment_variables || [],
  secrets_required: input.secrets_required || [],

  // Configuration files to generate
  config_files: [
    'vercel.json',
    'netlify.toml',
    'Dockerfile',
    'docker-compose.yml',
    '.env.example'
