  expected_status_codes: input.expected_status_codes || { '/': 200, '/api/health': 200 },

  // Thresholds
  max_response_time_ms: input.max_response_time_ms || 3000,
  required_uptime_percent: input.required_uptime_percent || 99
};

return { json: context };

// Historian Init - Archive Query Handler
const input = $input.first().json;

const context = {
  agent: 'historian',
  tier: 'support',
  hackathon_id: input.hackathon_id,
  triggered_at: input.triggered_at || new Date().toISOString(),

  // Query
  query: input.query || '',
  query_type: input.query_type || 'general', // general, code, decision, error

  // Archive context
  archive_ids: input.archive_ids || [],
  search_scope: input.search_scope || 'all', // all, recent, specific

  // Filters
  filters: {
    hackathon_type: input.hackathon_type || null,
    date_range: input.date_range || null,
    technologies: input.technologies || [],
    outcome: input.outcome || null // success, partial, failed
  },

  // Response preferences
  include_code_examples: input.include_code_examples !== false,
  include_lessons_learned: input.include_lessons_learned !== false,
  max_examples: input.max_examples || 3
};

return { json: context };

// Scribe Init - Experience Writer Handler
const input = $input.first().json;

const context = {
  agent: 'scribe',
  tier: 'support',
  hackathon_id: input.hackathon_id,
  triggered_at: input.triggered_at || new Date().toISOString(),

  // Hackathon summary
  hackathon_name: input.hackathon_name || '',
  hackathon_theme: input.hackathon_theme || '',
  duration_hours: input.duration_hours || 0,

  // Outcomes
  final_product: input.final_product || {},
  files_generated: input.files_generated || 0,
  agents_used: input.agents_used || [],

  // Metrics
  total_cost: input.total_cost || 0,
  total_tokens: input.total_tokens || 0,
  success_rate: input.success_rate || 0,

  // Key events
  key_decisions: input.key_decisions || [],
  challenges_faced: input.challenges_faced || [],
  solutions_found: input.solutions_found || [],

  // Output format
  output_format: input.output_format || 'markdown',
  include_metrics: input.include_metrics !== false,
  include_timeline: input.include_timeline !== false
};

return { json: context };

// Librarian Init - Repository Query Handler
const input = $input.first().json;

const context = {
  agent: 'librarian',
  tier: 'support',
  hackathon_id: input.hackathon_id,
  triggered_at: input.triggered_at || new Date().toISOString(),

  // Query
  query: input.query || '',
  query_type: input.query_type || 'structure', // structure, file, function, usage

  // Repository context
  repo_path: input.repo_path || '',
  file_tree: input.file_tree || [],

  // Search parameters
  search_pattern: input.search_pattern || '',
  file_types: input.file_types || [],

  // Response preferences
  include_file_contents: input.include_file_contents || false,
  include_dependencies: input.include_dependencies || true,
  include_usage_examples: input.include_usage_examples || true,
  max_depth: input.max_depth || 3
};

return { json: context };

